import axios from 'axios'
let serverUrl = process.env.REACT_APP_SERVER_URL
import * as Types from '../actions/types'

export const register = user => dispatch => {
    axios.post(`${serverUrl}/user/register`,user)
    .then(user => {
        console.log(user)
    })
    .catch(error => {
        dispatch({
            type:Types.USERS_ERROR,
            payload:{
                error : error.resp
            }
        })
    })
}
